class LogoCarousel{constructor(o,n){this.container=document.getElementById(o),this.dataUrl=n,this.companies=[],this.init()}async init(){try{console.log("🚀 Initializing Logo Carousel..."),await this.loadCompanies(),console.log(`📊 Loaded ${this.companies.length} company logos`),this.render(),console.log("✅ Logo carousel initialized successfully")}catch(o){console.error("❌ Error initializing logo carousel:",o),this.showFallback()}}async loadCompanies(){const o=await fetch(this.dataUrl);if(!o.ok)throw new Error(`HTTP error! status: ${o.status}`);this.companies=await o.json()}render(){if(!this.container||0===this.companies.length)return void console.log("❌ Container not found or no companies loaded");console.log("�� Rendering logo carousel with",this.companies.length,"companies");const o=this.companies.map(o=>this.renderLogoItem(o)).join(""),n=o+o;this.container.innerHTML=`\n            <div class="logo-track">\n                ${n}\n            </div>\n        `,this.bindEvents()}renderLogoItem(o){return`\n            <div class="logo-item" data-company="${o.company}">\n                <img src="${o.logo}" \n                     alt="${o.company} logo" \n                     class="company-logo-carousel"\n                     onload="this.classList.add('loaded')" \n                     onerror="this.style.display='none'; this.parentElement.style.display='none';">\n            </div>\n        `}bindEvents(){const o=this.container.querySelector(".logo-track"),n=this.container.querySelectorAll(".logo-item");o&&(o.addEventListener("mouseenter",()=>{o.style.animationPlayState="paused"}),o.addEventListener("mouseleave",()=>{o.style.animationPlayState="running"})),n.forEach(o=>{o.addEventListener("click",()=>{const n=o.dataset.company;console.log(`Clicked on ${n} logo`)})})}showFallback(){this.container&&(this.container.innerHTML='\n                <div class="logo-carousel-fallback">\n                    <p>Trusted by leading companies across industries</p>\n                    <div class="fallback-logos">\n                        <div class="fallback-logo">OLCI Engineering</div>\n                        <div class="fallback-logo">Marcopolo Spa</div>\n                        <div class="fallback-logo">Brandsider Srl</div>\n                        <div class="fallback-logo">360 Aligners Academy</div>\n                        <div class="fallback-logo">Giacomo Tomatis Srl</div>\n                        <div class="fallback-logo">Dynamo Spa</div>\n                    </div>\n                </div>\n            ')}}document.addEventListener("DOMContentLoaded",()=>{document.getElementById("logo-carousel")&&new LogoCarousel("logo-carousel","CMS/success-stories.json")});