async function loadOrbitalConfigurations(){const a=document.getElementById("orbital-configurations");if(a){a.innerHTML='<div class="orbital-loading">Loading server configurations...</div>';try{const a=await fetch("CMS/orbital.json");if(!a.ok)throw new Error(`HTTP error! status: ${a.status}`);renderOrbitalConfigurations(await a.json())}catch(n){console.error("Error loading orbital configurations:",n),a.innerHTML='\n            <div class="orbital-error">\n                <div class="orbital-error-icon">⚠️</div>\n                <h3>Error Loading Configurations</h3>\n                <p>Unable to load server configurations. Please try again later.</p>\n            </div>\n        '}}else console.error("Orbital configurations container not found")}function renderOrbitalConfigurations(a){const n=document.getElementById("orbital-configurations");if(!n||!a||0===a.length)return void(n.innerHTML='<div class="orbital-error">No configurations available</div>');const s=a.map(a=>`\n        <div class="orbital-config-card ${a.name.toLowerCase().includes("custom")?"custom-quote":""} fade-in">\n            <div class="orbital-config-header">\n                <h3 class="orbital-config-name">${a.name}</h3>\n                <p class="orbital-config-description">${a.description}</p>\n            </div>\n\n            <div class="orbital-config-specs">\n                <div class="orbital-spec-item">\n                    <span class="orbital-spec-label">CPU</span>\n                    <span class="orbital-spec-value">${a.specs.cpu}</span>\n                </div>\n                <div class="orbital-spec-item">\n                    <span class="orbital-spec-label">RAM</span>\n                    <span class="orbital-spec-value">${a.specs.ram}</span>\n                </div>\n                <div class="orbital-spec-item">\n                    <span class="orbital-spec-label">Storage</span>\n                    <span class="orbital-spec-value">${a.specs.storage}</span>\n                </div>\n                <div class="orbital-spec-item">\n                    <span class="orbital-spec-label">Network</span>\n                    <span class="orbital-spec-value">${a.specs.network}</span>\n                </div>\n                ${a.specs.gpu&&"None"!==a.specs.gpu?`\n                <div class="orbital-spec-item">\n                    <span class="orbital-spec-label">GPU</span>\n                    <span class="orbital-spec-value">${a.specs.gpu}</span>\n                </div>\n                `:""}\n            </div>\n\n            <div class="orbital-config-features">\n                <h4 class="orbital-features-title">Key Features</h4>\n                <ul class="orbital-features-list">\n                    ${a.features.map(a=>`\n                        <li class="orbital-feature-item">${a}</li>\n                    `).join("")}\n                </ul>\n            </div>\n\n            <div class="orbital-config-use-cases">\n                <h4 class="orbital-use-cases-title">Ideal For</h4>\n                <ul class="orbital-use-cases-list">\n                    ${a.use_cases.map(a=>`\n                        <li class="orbital-use-case-item">${a}</li>\n                    `).join("")}\n                </ul>\n            </div>\n\n            <div class="orbital-config-pricing">\n                <div class="orbital-pricing-monthly">\n                    <div class="price">${a.pricing.monthly}</div>\n                    <div class="period">per month</div>\n                </div>\n                <div class="orbital-pricing-setup">\n                    <div class="price">${a.pricing.setup}</div>\n                    <div class="label">setup fee</div>\n                </div>\n            </div>\n\n            <div class="orbital-config-availability">\n                <div class="orbital-availability-status ${a.availability.toLowerCase().replace(" ","-")}">\n                    <div class="orbital-availability-dot"></div>\n                    ${a.availability}\n                </div>\n            </div>\n\n            <div class="orbital-config-cta">\n                ${a.name.toLowerCase().includes("custom")?'<a href="contact.html" class="btn btn-primary">Get Custom Quote</a>':'<a href="contact.html" class="btn btn-primary">Request Quote</a>'}\n            </div>\n        </div>\n    `).join("");n.innerHTML=s;n.querySelectorAll(".orbital-config-card").forEach((a,n)=>{a.style.opacity="0",a.style.transform="translateY(20px)",setTimeout(()=>{a.style.transition="all 0.6s ease",a.style.opacity="1",a.style.transform="translateY(0)"},100*n)})}document.addEventListener("DOMContentLoaded",function(){loadOrbitalConfigurations()}),document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll('a[href^="#"]').forEach(a=>{a.addEventListener("click",function(a){a.preventDefault();const n=this.getAttribute("href").substring(1),s=document.getElementById(n);s&&s.scrollIntoView({behavior:"smooth",block:"start"})})})});